INSERT INTO `tp_article` VALUES ('1', 'HTML5 当中进度条 progress与meter的使用', '简单的实现的 进度条的的功能，使定时器来启动与关闭 ​', '<p style=\"text-align:center;\"><strong><br/></strong></p><p><br/></p><h2>HTML5 当中进度条 progress与meter的使用</h2><p>手动开启与停用进度条&nbsp;<br/></p><p><br/></p><p>什么都不说了先上代码:</p><pre class=\"brush:html;toolbar:false\">&lt;!DOCTYPE&nbsp;html&gt;\n&lt;html&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;utf-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;HTML5&nbsp;新元素&nbsp;progress的简单使用&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;i&nbsp;=&nbsp;0;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;delay&nbsp;=&nbsp;200;&nbsp;//2秒\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;timer;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;单击事件&nbsp;使用定时器来改变进度条的状态\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;btn(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;btValue&nbsp;=&nbsp;document.getElementsByTagName(&#39;input&#39;)[0];\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//定义初始进度\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;thread_one(){\n//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i&nbsp;&lt;=&nbsp;100){\n//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;i++;\n//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;updateProgress(i);\n//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n//&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(btValue.value&nbsp;==&nbsp;&#39;点击&#39;){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;btValue.value&nbsp;=&nbsp;&#39;暂停&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;timer&nbsp;=&nbsp;start(thread_one,delay);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}else{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;btValue.value&nbsp;=&nbsp;&#39;点击&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;stop(timer);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;document.getElementById(&#39;p&#39;).value=i;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i&nbsp;&gt;100){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;i&nbsp;=&nbsp;0;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//开始\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;start(cod,delay){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;return&nbsp;timer&nbsp;=&nbsp;setInterval(cod,delay);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//结束\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;stop(timer){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;timer&nbsp;=&nbsp;clearInterval(timer);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//更新\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;thread_one(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(i&nbsp;&lt;=100){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;i++;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;updateProgress(i);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;/**\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;更新进度状态\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*&nbsp;@param&nbsp;newValue&nbsp;表示当前的的值\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;updateProgress(newValue){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;progressBar&nbsp;=&nbsp;document.getElementById(&#39;p&#39;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;meterBar&nbsp;=&nbsp;document.getElementsByTagName(&#39;meter&#39;)[0];\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;meterBar.value&nbsp;=&nbsp;newValue;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;progressBar.value&nbsp;=&nbsp;newValue;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//第一个span标签\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;progressBar.getElementsByTagName(&#39;span&#39;)[0].textContent&nbsp;=&nbsp;newValue;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/script&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;body&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;section&gt;\n&nbsp;&nbsp;&lt;!--&nbsp;progress元素--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;h2&gt;progress元素的使用&lt;/h2&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;p&gt;完成的百分比&nbsp;&lt;progress&nbsp;style=&quot;background-color:red&quot;&nbsp;id=&quot;p&quot;&nbsp;max=&quot;100&quot;&gt;&lt;span&gt;0&lt;/span&gt;%&lt;/progress&gt;&lt;/p&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;input&nbsp;type=&quot;button&quot;&nbsp;onclick=&quot;btn()&quot;&nbsp;value=&quot;点击&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;br&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;hr&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;h2&gt;meter元素的使用&lt;/h2&gt;\n&lt;!--meter元素--&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;meter&nbsp;value=&quot;0&quot;&nbsp;min=&quot;0&quot;&nbsp;max=&quot;100&quot;&nbsp;low=&quot;10&quot;&nbsp;high=&quot;90&quot;&nbsp;optimum=&quot;80&quot;&gt;&lt;/meter&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;meter&gt;&lt;/meter&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/section&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;/body&gt;\n&lt;/html&gt;</pre><p>下面是效果图:</p><p><img src=\"/upload/image/20170207/1486438310.gif\" title=\"1486438310.gif\" alt=\"progress.gif\"/></p><p><a href=\"/progress.html\" target=\"_blank\" title=\"在线实例\">进度条实例在线查看</a></p>', '懒人作者', '/upload/image/20170207/1486438310.gif', null, '/upload/thumb/20170207/1486438337.gif', 'progress,meter', '18', '0', '', '1', '1', '7', '7', '1', '1486438337', '1486542487');
INSERT INTO `tp_article` VALUES ('2', 'Range对象的使用', '对Range对象使用的几个小实例,如 选择 复制 比较操作', '<h2><strong>一：Range对象的概念</strong></h2><p><br/></p><p><strong>&nbsp;</strong>Range对象代表页面上一段连续区域，通过Range对象，可以获取或是修改页面上的任何区域,使用如下代码进创建一个Range对象,创建代码如下:</p><pre class=\"brush:js;toolbar:false\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;range&nbsp;=&nbsp;document.createRange();</pre><p>&nbsp;在每个html5中,每一个浏览器窗口及每一个窗口中都有一个selectionc对象,代表用户鼠标在页面中所选取的区域(<span style=\"color: rgb(255, 0, 0);\">注意:IE9以下不支持</span>),可以使如下语句创建selection对象,代码如下:<br/></p><pre class=\"brush:js;toolbar:false\">var&nbsp;selection&nbsp;=&nbsp;document.getSelection()&nbsp;||&nbsp;window.getSelection();</pre><p>每一个selection对象都有一个或者多个Range对象，每一个Range都代表着用户鼠标所选取范围内的一段连续区域(<span style=\"color: rgb(255, 0, 0);\">注意:firefox中可以使用连续选中多块区域</span>),可以通过selection对象的getRangeAt()方法来获取selection对象的某个Range对象,代码如下:<br/></p><pre class=\"brush:js;toolbar:false\">var&nbsp;range&nbsp;=&nbsp;document.getSelection().getRangeAt(index);</pre><p>getRangeAt方法有一个参数index,代表该Range对象的序列号;我们可以通过Selection对象的rangeCount属性的值来判断用户是否选取了内容，具体如下:</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p>当用户没有按下鼠标时候,该参数的值为0.</p></li><li><p>当用户按下鼠标的时候,该参数为1.</p></li><li><p>当用户使用鼠标同时按住ctrl键时选取了一个或者多个区域时候,该参数代表用户选取区域的数量.</p></li><li><p>当用户取消区域的选取时,该属性值代表1,代表页面上存一个空的Range对象</p></li></ul><p><br/></p><h2><strong>一：Range对象的属性与方法</strong></h2><p>&nbsp;属性如下:</p><ul class=\" list-paddingleft-2\" style=\"list-style-type: disc;\"><li><p><strong>collapsed(boolean)</strong>: &nbsp; 用于判断Range对象所代表的区域的开始点与结束点是否处于相同的位置,如果值相同该属性返回的是true。</p></li><li><p><strong>commonAncestorContainer(node): </strong>&nbsp; &nbsp;用于返回Range对象所代表区域位于什么节点之中。</p></li><li><p><strong>endContainer(node): </strong>&nbsp; &nbsp;用于返回Range对象所代表区别的终点。</p></li><li><p><strong>endOffset(int): </strong>&nbsp; &nbsp;用于返回Range对象所代表区域的终点与包含该终点的节点与起点之间的距离,返回是int。</p></li><li><p><strong>startContainer(node):</strong> &nbsp; 用于返回Range对象所代表的区域的起点位于什么节点之中。</p></li><li><p><strong>startOffset(int):</strong> &nbsp; &nbsp;用于返回Range对象所代表的区域的起点与包含该起点的节点的起点之间的距离,返回int。</p></li><li><p><strong>selectNode(node)：</strong> &nbsp; &nbsp;Range对象的selectNode方法用于将Range对象的起点指定为某个节点的起点，将Range对象的终点指定为该节点的终点，使Range对象所代表的区域中包含该节点。使用方法如下(rangeObj代表Range对象):</p><pre class=\"brush:js;toolbar:false\">rangeObj.selectNode(node);//该方法参数只有一个，代表页面中的一个节点</pre><p><br/></p></li><li><p><strong>selectNodeContents()：</strong> &nbsp;用于将Range对象的起点指定为某个节点中的所有内容的起点，将Range对象的终点指定为该节点所有内容的终点，使Range对象所代表的区域中包含该节点的所有内容,使用方法如下:</p><pre class=\"brush:js;toolbar:false\">rangeObj.selectNodeContents(node);//该方法参数只有一个，代表页面中的一个节点</pre><p><br/></p></li><li><p><strong>deleteContents()</strong>：用于将Range对象中所包含的内容从页面中删除。使用方法如下:</p><pre class=\"brush:js;toolbar:false\">rangeObj.deleteContents();</pre><p><br/></p></li><li><p><strong>setStart(node,curIndex)</strong> &nbsp; &nbsp;用于将某个节点中的某处位置指定为Range对象所代表区域的起点位置</p><pre class=\"brush:js;toolbar:false\">rangeObj.setStart(node,curIndex);//该方法第一个参数node代表一个节点,第一个参数代表是一个数字起始位置</pre><p><br/></p><p><br/></p></li><li><p><strong>setEnd(node,curIndex)</strong>&nbsp; &nbsp; &nbsp;用于将某个节点中的某处位置指定Range对象所代表区域的结束位置。</p><pre class=\"brush:js;toolbar:false\">rangeObj.setEnd(node,curIndex);//该方法第一个参数node代表一个节点,第一个参数代表是一个数字结束位置</pre><p><br/></p></li><li><p><strong>setStartBefore(node)：</strong> &nbsp; &nbsp;用于将某个节点的起点位置指定为Range对象所代表区域的起点位置。</p><pre class=\"brush:js;toolbar:false\">rangeObj.setStartBefore(node);//该方法参数只有一个，代表页面中的一个节点</pre><p><br/></p></li><li><p><strong>setStartAfter(node)：</strong> &nbsp; &nbsp; 用于将某个节点的终点位置指定为Range对象所代表区域的起点位置。</p><pre class=\"brush:js;toolbar:false\">rangeObj.setStartAfter(node);//该方法参数只有一个，代表页面中的一个节点</pre><p><br/></p></li><li><p><strong>setEndBefore(node)：</strong> &nbsp; &nbsp;用于将某个节点的起点位置指定为Range对象所代表区域的终点位置。</p><pre class=\"brush:js;toolbar:false\">rangeObj.setEndBefore(node);//该方法参数只有一个，代表页面中的一个节点</pre><p><br/></p></li><li><p><strong>setEndAfter(node)：</strong> &nbsp; &nbsp; 用于将某个节点的终点位置指定为Range对象所代表区域的终点位置。</p><pre class=\"brush:js;toolbar:false\">rangeObj.setEndAfter(node);//该方法参数只有一个，代表页面中的一个节点</pre><p><br/></p></li><li><p><strong>cloneRange() </strong>&nbsp; &nbsp; Range对象的cloneRange方法用于对当前的Range对象进行复制，该方法返回复制的Range对象。</p><pre class=\"brush:js;toolbar:false\">var&nbsp;rangeClone&nbsp;=&nbsp;rangeObj.cloneRange();</pre><p><br/></p></li><li><p><strong>cloneContents() </strong>&nbsp; &nbsp; 该方法用于在页面上追加一段HTML代码，并且将Range对象所代表区域中的HTML代码克隆到被追加的html代码中。</p><pre class=\"brush:js;toolbar:false\">var&nbsp;rangeClone&nbsp;=&nbsp;rangeObj.cloneContents();</pre><p><br/></p></li><li><p><strong>extractContents() </strong>&nbsp; &nbsp;用于将Range对象所代表区域中的html代码克隆到一个DocumentFragment对象中，然后从页面中删除这段HTML代码。</p><pre class=\"brush:js;toolbar:false\">var&nbsp;documentFragment&nbsp;=&nbsp;rangeObj.extractContents();</pre><p><br/></p></li><li><p><strong>insertNode(node)</strong> &nbsp; &nbsp;该方法用于将指定的节点插入到某个Range对象所代表的区域中，插入位置为Range对象所代表区域的起点位置，如果该节点已经存在于页面中，该节点将被移动到Range对象代表的区域的起点处。<br/></p><pre class=\"brush:js;toolbar:false\">rangeObj.insertNode(node);//该方法参数只有一个，代表页面中的一个节点</pre><p>下面一些实际操作代码(<a href=\"/range.html\" target=\"_blank\" title=\"range实例\">点击我在线查看实例</a>):</p><pre class=\"brush:html;toolbar:false\">&lt;!DOCTYPE&nbsp;html&gt;\n&lt;html&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&lt;head&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;meta&nbsp;charset=&quot;utf-8&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;title&gt;Rang元素的使用&lt;/title&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;style&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#div{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background-color:&nbsp;bisque;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;300px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;150px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;text-align:&nbsp;center;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#myDiv{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;color:&nbsp;red;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#new{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background-color:&nbsp;antiquewhite;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;300px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;50px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#old{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background-color:&nbsp;burlywood;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;300px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;50px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;#move{\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;background-color:&nbsp;bisque;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;width:&nbsp;300px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;height:&nbsp;50px;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;/style&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&lt;script&nbsp;type=&quot;text/javascript&quot;&gt;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//创建rangeObj&nbsp;对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;rangeObj&nbsp;=&nbsp;document.createRange();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取选中的内容\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;function&nbsp;rangTest(){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;html=&#39;&#39;;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;showRangeDiv&nbsp;=&nbsp;document.getElementById(&#39;showRange&#39;);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//获取选中的对象\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;selection&nbsp;=&nbsp;document.getSelection();\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//目前只有firefox支持多选\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(selection.rangeCount&nbsp;&gt;&nbsp;0){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;for(var&nbsp;i=0;i&lt;selection.rangeCount;i++){\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;//选中的区域有几个\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;var&nbsp;range&nbsp;=&nbsp;selection.getRangeAt(i);\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;html&nbsp;+=&nbsp;&quot;您选取